var five = require("../");
var board = new five.Board();

board.on("ready", function() {

  var config = {
    controller: "PCA9685",
    address: 0x40,
    center: true
  };

  var pins = [0, 4, 11, 15];

  var servos = new five.Servo.Array(
    pins.map(function(pin) {
      return new five.Servo(Object.assign({}, config, { pin: pin }));
    })
  );

  this.repl.inject({
    s: servos
  });
});

