var five = require("../lib/johnny-five");
var board = new five.Board();

board.on("ready", function() {

  var motor = new five.Motor({
    controller: "PCA9685",
    address: 0x60,
    pins: {
      pwm: 8,
      dir: 9,
      cdir: 10
    }
  });

  motor.fwd(255).start();

  this.repl.inject({
    m: motor
  });

});

